import { Bitmopay } from '@bitmopay/sdk';

const bitmopay = new Bitmopay('YOUR_API_KEY');

const payment = await bitmopay.payments.create({
  amount: 100,
  currency: 'USD',
  description: 'Payment for Order #1234',
  redirectUrl: 'https://example.com/success'
});

console.log(payment.redirectUrl);
