// Create checkout with buyer protection via Bitmopay API
const response = await fetch('https://api.bitmopay.com/v1/checkout', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'X-API-Key': 'YOUR_API_KEY'
  },
  body: JSON.stringify({
    amount: 499.99,
    currency: 'USD',
    paymentMethods: ['BTC', 'ETH', 'USDC'],
    buyerProtection: true,  // Enable 7-day escrow
    escrowDays: 7,         // Optional: customize escrow period
    redirectUrl: 'https://your-store.com/thank-you',
    webhookUrl: 'https://your-store.com/api/webhook'
  })
});

const data = await response.json();
// Redirect customer to protected payment page
window.location.href = data.checkoutUrl;
