// Create payment via Bitmopay API
const response = await fetch('https://api.bitmopay.com/v1/checkout', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'X-API-Key': 'YOUR_API_KEY'
  },
  body: JSON.stringify({
    amount: 99.99,
    currency: 'USD',
    paymentMethods: ['BTC', 'ETH', 'USDC'],
    redirectUrl: 'https://your-store.com/thank-you',
    webhookUrl: 'https://your-store.com/api/webhook'
  })
});

const data = await response.json();
// Redirect customer to payment page
window.location.href = data.checkoutUrl;
