// Create subscription plan via Bitmopay API
const response = await fetch('https://api.bitmopay.com/v1/subscriptions', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'X-API-Key': 'YOUR_API_KEY'
  },
  body: JSON.stringify({
    name: 'Premium Plan',
    amount: 49.99,
    currency: 'USD',
    interval: 'monthly',
    description: 'Access to all premium features',
    webhookUrl: 'https://your-saas.com/api/subscription-webhook'
  })
});

const data = await response.json();
// Share subscription link with customer
console.log(`Subscription URL: ${data.subscriptionUrl}`);
